CREATE DATABASE IF NOT EXISTS cadet_management;

USE cadet_management;

CREATE TABLE Objectives (
    id INT AUTO_INCREMENT PRIMARY KEY,
    category VARCHAR(255),
    objective_number VARCHAR(10),
    description TEXT,
    bc ENUM('P1', 'P2', 'P3'),
    bcl ENUM('P1', 'P2', 'P3'),
    icl ENUM('P1', 'P2', 'P3'),
    scl ENUM('P1', 'P2', 'P3')
);

